import { isDef } from './utils';

export interface AxiosSugarConfigOptions {
  isResend?: boolean;
  resendDelay?: number;
  resendTimes?: number;
  isSave?: boolean;
  prop?: string;
}

export default class AxiosSugarConfig {
  isResend = false;
  resendDelay = 1000;
  resendTimes = 3;
  isSave = false;
  prop = 'custom';
  constructor (options?: AxiosSugarConfigOptions) {
    options = options || {};
    for (const key of Object.keys(options)) {
      if (isDef(key)) {
        this[key] = options[key];
      } else {
        console.error(`[axios sugar]: the option ${key} is not valid.`);
      }
    }
  }
}

export function capitalize (str: string): string {
  return str.substring(0, 1).toUpperCase() + str.substring(1);
}

export function isDef (value: any): boolean {
  return typeof value !== 'undefined';
}

export function isStr (value: any): boolean {
  return typeof value === 'string';
}

export function getDurationMS (a: number, b: number): number {
  return a - b;
}

import { AxiosInstance, AxiosStatic } from '../vendor/axios';
import AxiosSugarConfig from '../AxiosSugarConfig';
import { AxiosSugarStorage, AxiosSugarInnerStorage } from '../AxiosSugarStorage';
import { AxiosSugarRequestStack, AxiosStack } from '../stack';
import responseInterceptors from './ResponseInterceptors';
import requestInterceptors from './Requestinterceptors';
import AxiosSugarLifeCycle from '../AxiosSugarLifeCycle';

interface AxiosSugarOptions {
  config?: AxiosSugarConfig;
  storage?: AxiosSugarStorage;
  lifecycle?: AxiosSugarLifeCycle;
}

export default class AxiosSugar {
  private stack: AxiosSugarRequestStack = new AxiosSugarRequestStack();
  axios: AxiosInstance | AxiosStatic;
  config: AxiosSugarConfig = new AxiosSugarConfig();
  storage: AxiosSugarStorage = new AxiosSugarInnerStorage();
  lifecycle: AxiosSugarLifeCycle = new AxiosSugarLifeCycle();
  constructor (axios: AxiosInstance | AxiosStatic, options: AxiosSugarOptions = {}) {
    this.axios = axios;
    ['config', 'storage', 'lifecycle'].forEach(option => {
      if (options[option]) {
        this[option] = options[option];
      }
    });
    this.init();
  }
  private init (): void {
    requestInterceptors(this, this.stack);
    responseInterceptors(this, this.stack);
  }
}

// the axios has been used
const usedAxios = new AxiosStack();

export function factory (
  axios: AxiosInstance | AxiosStatic,
  options: AxiosSugarOptions = {}
): undefined | AxiosSugar {
  if (usedAxios.contains(axios)) {
    console.error('[axios-sugar]: an axios static or instance only can call factory once.');
  } else {
    usedAxios.push(axios)
    return new AxiosSugar(axios, options);
  }
}

import { AxiosRequestConfig, AxiosInstance, AxiosStatic } from './vendor/axios';

export class Stack<el> {
  protected stack: el[] = [];
  push (el: el): number {
    return this.stack.push(el);
  }
  pop (): el {
    return this.stack.pop();
  }
  contains (el: el): boolean {
    return this.stack.indexOf(el) >= 0;
  }
  remove (el: el): el {
    return this.stack.splice(this.indexOf(el), 1)[0];
  }
  indexOf (el: el): number {
    return this.stack.indexOf(el);
  }
}

export class AxiosSugarRequestStack extends Stack<AxiosRequestConfig> {
  forEach (cb): void {
    this.stack.forEach((conf, confIdx, thisArg) => {
      cb.call(conf, conf, confIdx, thisArg);
    });
  }
}

type AxiosEl = AxiosStatic | AxiosInstance;

export class AxiosStack extends Stack<AxiosEl> {}

import AxiosSugar from './AxiosSugar';
import { AxiosSugarRequestStack } from '../stack';
import { notUndef, genSymbol } from './utils';
import { AxiosSugarError } from './SugarError';
import { isStr } from '../utils';

export default function (
  sugar: AxiosSugar,
  stack: AxiosSugarRequestStack
): void {
  const axios = sugar.axios;
  const storage = sugar.storage;
  const conf = sugar.config;
  const lifecycle = sugar.lifecycle;
  let error: AxiosSugarError | Error;

  axios.interceptors.response.use(res => {
    const config = res.config;
    const resData = res.data;

    const cycleRes = lifecycle.beforeResponse(res);
    if (!cycleRes.state) {
      error = { reason: 'beforeResponseBreak', message: cycleRes.message };
      return Promise.reject(error);
    }

    if (config) {
      stack.remove(config);
    }

    // get custom option
    const custom = config.custom;
    let isSave;
    if (custom) {
      isSave = notUndef(custom.isSave, conf.isSave);
    }

    // generate symbol string
    if (isSave) {
      const symbol = genSymbol(config);
      storage.set(symbol, resData);
    }

    return Promise.resolve(resData);
  }, err => {
    // if AxiosSugarError
    const reason = err.reason;

    if (reason) {
      return reason === 'saved' ? Promise.resolve(err.data) : Promise.reject(err);
    }

    const config = err.config;
    // axios error
    if (config) {
      // remove this request in stack
      stack.remove(config);

      // get custom options
      const custom = config[conf.prop];
      let isResend = conf.isResend,
        resendDelay = conf.resendDelay,
        resendTimes = conf.resendTimes,
        curResendTimes = 0;
      if (custom) {
        isResend = notUndef(custom.isResend, isResend);
        resendDelay = notUndef(custom.resendDelay, resendDelay);
        resendTimes = notUndef(custom.resendTimes, resendTimes);
        curResendTimes = notUndef(custom.curResendTimes, 0);
      }
      if (isResend) {
        if (curResendTimes < resendTimes) {
          return new Promise(resolve => {
            setTimeout(() => {
              if (!custom) {
                config.custom = {};
              }
              config.custom.curResendTimes = ++curResendTimes;
              if (isStr(config.data)) {
                config.data = JSON.parse(config.data);
              }
              return resolve(axios.request(config));
            }, resendDelay);
          });
        } else {
          error = {reason: 'resendEnd', message: `Can't get a response.`};
          return Promise.reject(error);
        }
      } else {
        return Promise.reject(err);
      }
    }
  });
}

import { AxiosRequestConfig } from '../vendor/axios';
import { isDef } from '../utils';

// where the sending data in ? (params or data)
function sendDataWay (method: string): 'data' | 'params' | 'both' {
  const isInData = ['post', 'put', 'patch'].indexOf(method) >= 0,
    isInParams = method === 'get';
  return isInData ? 'data' : (isInParams ? 'params' : 'both');
}

export function normalizeProp (config: AxiosRequestConfig, prop = 'custom'): AxiosRequestConfig {
  // if custom prop in data
  if (sendDataWay(config.method) === 'data' && config.data) {
    const propVal = config.data[prop];
    if (propVal) {
      config[prop] = propVal;
      delete config.data[prop];
    }
  }
  return config;
}

export function genSymbol (config: AxiosRequestConfig): string {
  let { url } = config;
  const { method } = config;
  // the data send before
  let data;

  function getParamsSymbolData (params: object): string {
    let data = '';
    if (/\?/.test(url)) {
      const part = url.split('?');
      url = part[0];
      data += part[1];
    }
    if (params) {
      for (const [key, val] of Object.entries(params)) {
        if (data !== '') data += '&';
        data += `${key}=${val}`;
      }
    }
    return data;
  }

  function getDataSymbolData (data: object): string {
    return isDef(data) ? JSON.stringify(data) : '';
  }

  // get the sent data
  switch (sendDataWay(method)) {
    case 'params':
      data = getParamsSymbolData(config.params);
      break;
    case 'data':
      data = getDataSymbolData(config.data);
      break;
    case 'both':
      data = getParamsSymbolData(config.params) || getDataSymbolData(config.params);
  }

  return `method=${method}&url=${url}&data=${data}`;
}

export function notUndef<T = any, D = any> (targetVal: T, defaultVal: D): T | D {
  return typeof targetVal === 'undefined' ? defaultVal : targetVal;
}

import { AxiosSugarRequestStack } from "../stack";
import { genSymbol, notUndef, normalizeProp } from './utils';
import AxiosSugar from "./AxiosSugar";
import { AxiosSugarError } from './SugarError';

export default function (
  sugar: AxiosSugar,
  stack: AxiosSugarRequestStack
): void {
  const axios = sugar.axios;
  const storage = sugar.storage;
  const conf = sugar.config;
  const lifecycle = sugar.lifecycle;
  let error: AxiosSugarError | Error;

  axios.interceptors.request.use(config => {
    config = normalizeProp(config, conf.prop);

    if (stack.contains(config)) {
      error = { reason: 'existed' };
      return Promise.reject(error);
    }

    // get custom options
    const custom = config[conf.prop];
    let isSave = conf.isSave;
    if (custom) {
      isSave = notUndef(custom.isSave, isSave);
    }
    if (isSave) {
      const storageRes = storage.get(genSymbol(config));
      if (storageRes) {
        error = { reason: 'saved', data: storageRes };
        return Promise.reject(error);
      }
    }

    const cycleRes = lifecycle.beforeRequest(config);

    if (!cycleRes.state) {
      error = { reason: 'beforeRequestBreak', message: cycleRes.message };
      return Promise.reject(error);
    }

    // send request
    stack.push(config);
    return Promise.resolve(config);
  }, err => {
    Promise.reject(err);
  });
}

import { AxiosRequestConfig, AxiosResponse } from './vendor/axios/index';

interface AxiosSugarLifeCycleResult {
  state: boolean;
  message: string;
}

export default class AxiosSugarLifeCycle {
  /* eslint-disable @typescript-eslint/no-unused-vars */
  beforeRequest (conf: AxiosRequestConfig): AxiosSugarLifeCycleResult {
    return {
      state: true,
      message: ''
    };
  }
  beforeResponse (res: AxiosResponse): AxiosSugarLifeCycleResult {
    return {
      state: true,
      message: ''
    };
  }
}
