import defaults, { AxiosSugarConfig } from '../defaults';
import axios from 'axios'
import { AxiosSugarInterceptorManager } from './AxiosSugarInterceptorManager';
import { AxiosRequestConfig, AxiosError, AxiosInstance } from 'axios/index';
import dispatchRequest from './dispatchRequest'
import initInterceptors from './initInterceptors';
import { repeatTag } from './repeat';
import HttpStatusProcessor from './HttpStatusProcessor';
import {  } from 'axios/index';

const { deepMerge, merge } = require('axios/lib/utils');

export interface Interceptors {
  request: AxiosSugarInterceptorManager;
  response: AxiosSugarInterceptorManager;
}

type Event = 'beforeRetry' | 'beforeStore';

interface Events {
  beforeRetry?: Function;
  beforeStore?: Function;
  timeout?: Function;
}

class AxiosSugarPrototype {
  httpStatusProcessor: HttpStatusProcessor;
  create: (axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => AxiosSugar;
  request: (axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  get: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  post: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  head: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  options: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  delete: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  put: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  patch: (url: string, axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => Promise<any>;
  on: (event: Event, fn: Function) => void;
  off: (event: Event, fn: Function) => Boolean;
  repeatTag: (axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) => String;
  isCancel: (err: AxiosError) => Boolean;

  constructor () {
    this.httpStatusProcessor = new HttpStatusProcessor();
  }
}

export class AxiosSugar extends AxiosSugarPrototype {
  axios: AxiosInstance;
  interceptors: Interceptors;
  config: AxiosSugarConfig;
  events: Events;
  // 兼容axios的axios.get()和axios.get('', {})的写法，只在后面添加一个自定义的参数
  // axiosSugar.get({}, {timeout: 1000})和axios.get('', {}, {timeout: 1000})
  constructor (axiosConfig?: AxiosRequestConfig, config?: AxiosSugarConfig) {
    super();
    // config.axios可以是undefined
    this.axios = axios.create(axiosConfig);
    this.config = config || defaults;
    this.events = {};
    this.interceptors = {
      request: new AxiosSugarInterceptorManager(),
      response: new AxiosSugarInterceptorManager()
    };
    // 初始化当前拦截器
    initInterceptors(this);
  }
}

AxiosSugar.prototype.create = function (
  axiosConfig?: AxiosRequestConfig,
  config?: AxiosSugarConfig
): AxiosSugar {
  if (config) {
    config = deepMerge(defaults, config);
  }
  return new AxiosSugar(axiosConfig, config);
};

AxiosSugar.prototype.request = function (
  axiosConfig?: AxiosRequestConfig,
  config?: AxiosSugarConfig
): Promise<any> {
  config = config ? deepMerge(defaults, config) : this.config;

  let chain = [dispatchRequest.bind(this), undefined];
  // 传递参数给dispatchRequest和interceptors
  let promise: Promise<any> = Promise.resolve({axios: axiosConfig, sugar: config});

  this.interceptors.request.each((interceptor) => {
    chain.unshift(interceptor.fulfilled, interceptor.rejected);
  })

  this.interceptors.response.each((interceptor) => {
    chain.push(interceptor.fulfilled, interceptor.rejected);
  })

  while (chain.length) {
    promise = promise.then(chain.shift(), chain.shift());
  }
  
  return promise;
};

['delete', 'get', 'head', 'options'].forEach(function (method) {
  AxiosSugar.prototype[method] = function (
    url: string,
    axiosConfig: AxiosRequestConfig,
    config: AxiosSugarConfig
  ) {
    return this.request(merge(axiosConfig || {}, {
      method: method,
      url: url
    }), config);
  };
});

['post', 'put', 'patch'].forEach(function (method) {
  AxiosSugar.prototype[method] = function (
    url: string, data,
    axiosConfig: AxiosRequestConfig,
    config: AxiosSugarConfig
  ) {
    return this.request(merge(axiosConfig || {}, {
      method: method,
      url: url,
      data: data
    }), config);
  };
});

AxiosSugar.prototype.repeatTag = repeatTag;

AxiosSugar.prototype.on = function (event: Event, fn: Function) {
  this.events[event] = fn;
};

AxiosSugar.prototype.off = function (event: Event, fn: Function): Boolean {
  if (this.events[event] === fn) {
    this.events[event] = undefined;
    return true;
  }
  return false;
}

AxiosSugar.prototype.isCancel = function (err: AxiosError): Boolean {
  return axios.isCancel(err);
}

interface retry {
  enable?: Boolean;
  count?: Number;
}

export interface AxiosSugarConfig {
  timeout?: Number;
  retry?: retry,
  save?: Boolean;
}

export default {
  timeout: 2000,
  save: false,
  retry: {
    enable: false,
    count: 3
  },
}

interface ManagerHandler {
  fulfilled: Function;
  rejected: Function;
}

export class AxiosSugarInterceptorManager {
  private handlers: Array<ManagerHandler>;
  constructor () {
    this.handlers = [];
  }

  use (fulfilled: Function, rejected: Function) {
    this.handlers.push({
      fulfilled,
      rejected
    });

    return this.handlers.length - 1;
  }

  eject (i: number) {
    if (this.handlers[i]) {
      this.handlers[i] = null
    }
  }

  each (fn: Function) {
    this.handlers.forEach((handler: ManagerHandler) => {
      if (handler !== null) {
        fn(handler)
      }
    })
  }
}

import { AxiosRequestConfig, AxiosError, AxiosResponse } from 'axios/index';
import { AxiosSugarConfig } from '../defaults';

export interface MiddleRequestConfig {
  axios: AxiosRequestConfig;
  sugar: AxiosSugarConfig;
  index: number;
}

export interface MiddleResponseConfig {
  response: AxiosResponse;
  sugar: AxiosSugarConfig;
  index: number;
}

export interface MiddleResponseError {
  reason: AxiosError;
  sugar: AxiosSugarConfig;
  index: number;
}

export default function (config?: MiddleRequestConfig) {
  return this.axios.request(config.axios).then((response) => ({
    response,
    sugar: config.sugar
  }), (reason) => Promise.reject({
    reason,
    index: config.index,
    sugar: config.sugar
  }));
}

import { AxiosSugar } from './AxiosSugar';
import { MiddleRequestConfig, MiddleResponseConfig, MiddleResponseError } from './dispatchRequest';
import repeat from './repeat';
import MiddleData from './MiddleData';

export default function initInterceptors (axiosSugar: AxiosSugar) {
  axiosSugar.interceptors.request.use((config: MiddleRequestConfig) => {
    // 处理重复请求
    config.index = repeat(config);

    return config;
  }, (err: Error) => {
    return Promise.reject(err);
  });

  axiosSugar.interceptors.response.use((config: MiddleResponseConfig) => {
    // 删除已完成请求的中间数据
    if (MiddleData.tags[config.index] !== null) {
      MiddleData.tags[config.index] = null;
    }

    return axiosSugar.httpStatusProcessor.dispatch(config.response.status, config);
  }, (err: MiddleResponseError) => {
    let result = err;

    // 只处理响应相关的错误
    if (err.reason.response) {
      result = axiosSugar.httpStatusProcessor.dispatch(err.reason.response.status, err);
    }

    return result instanceof Promise ? result : Promise.reject(result || err);
  });
}

import { AxiosRequestConfig } from 'axios/index';
import { MiddleRequestConfig } from './dispatchRequest';
import { AxiosSugarConfig } from '../defaults';
import axios from 'axios';
import MiddleData from './MiddleData';

export function repeatTag (
  axiosConfig: AxiosRequestConfig,
  config?: AxiosSugarConfig
): String {
  return `method=${axiosConfig.method},url=${axiosConfig.url}`;
}

// 判断是否重复请求
export default function (
  config: MiddleRequestConfig
): number {
  const tag = repeatTag(config.axios, config.sugar);
  const i = MiddleData.tags.indexOf(tag);

  // 取消之前的请求
  if (i >= 0) {
    MiddleData.cancels[i]();
    MiddleData.tags[i] = null;
    MiddleData.cancels[i] = null;
  }

  const source = axios.CancelToken.source();
  config.axios.cancelToken = source.token;
  MiddleData.cancels.push(source.cancel);

  return MiddleData.tags.push(tag) - 1;
}

/**
 * 用于保存chain中产生的数据
 */
export default {
  tags: [],
  cancels: []
}

import { log, isDev } from './utils';
import { AxiosError } from 'axios/index';

class HttpStatusProcessorPrototype {
  private statusTable: Object;
  private reservedCodes: string[];
  [key: string]: any;

  constructor () {
    this.statusTable = {
      200: this.onOk,
      201: this.onCreated,
      202: this.onAccepted,
      203: this.onNonAuthoritativeInformation,
      204: this.onNoContent,
      205: this.onResetContent,
      206: this.onPartialContent,
      207: this.onMultiStatus,
      400: this.onBadRequest,
      401: this.onUnauthorized,
      403: this.onForbidden,
      404: this.onNotFound,
      405: this.onMethodNotAllow,
      406: this.onNotAcceptable,
      407: this.onProxyAuthenticationRequired,
      408: this.onTimeout,
      409: this.onConflict,
      500: this.onInternalServerError,
      501: this.onNotImplemented,
      502: this.onBadGateway
    }

    this.reservedCodes = Object.keys(this.statusTable);
  }

  setStatusHandler (status: string, fn: Function): Boolean {
    if (this.reservedCodes.indexOf(status) < 0) {
      this.statusTable[status] = fn;
      return true;
    } else if (isDev()) {
      log(`can't set the handler of http status code ${status}.`);
    }
    return false;
  }

  // 分配给对应的函数处理
  dispatch (status, payload) {
    const firstCode = status.toString().substr(0, 1);
    // result
    let result = payload.response;
    // onXXXBefore function
    if (this[`on${firstCode}XXBefore`]) {
      result = this[`on${firstCode}XXBefore`](status, payload, result);
    }
    if (this.statusTable[status]) {
      result = this.statusTable[status](status, payload, result);
    }
    if (this[`on${firstCode}XXAfter`]) {
      result = this.statusTable[status](status, payload, result);
    }
    return result;
  }
}

/**
 * 处理错误的类
 */
class HttpStatusProcessor extends HttpStatusProcessorPrototype {}

const errorhandlers = [
  'BadRequest',
  'Unauthorized',
  'Forbidden',
  'NotFound',
  'MethodNotAllow',
  'NotAcceptable',
  'ProxyAuthenticationRequired',
  'Timeout',
  'Conflict',
  'InternalServerError',
  'NotImplemented',
  'BadGateway'
];

errorhandlers.forEach(h => {
  HttpStatusProcessor.prototype['on' + h] = function (status, err, result): AxiosError {
    if (isDev()) {
      log(err.message);
    }
    return err.reason;
  };
});

export default HttpStatusProcessor;

import { AxiosRequestConfig } from '../vendor/axios';
import { isDef } from '../utils';

// where the sending data in ? (params or data)
function sendDataWay (method: string): 'data' | 'params' | 'both' {
  const isInData = ['post', 'put', 'patch'].indexOf(method) >= 0,
    isInParams = method === 'get';
  return isInData ? 'data' : (isInParams ? 'params' : 'both');
}

export function normalizeProp (config: AxiosRequestConfig, prop = 'custom'): AxiosRequestConfig {
  // if custom prop in data
  if (sendDataWay(config.method) === 'data' && config.data) {
    const propVal = config.data[prop];
    if (propVal) {
      config[prop] = propVal;
      delete config.data[prop];
    }
  }
  return config;
}

export function genSymbol (config: AxiosRequestConfig): string {
  let { url } = config;
  const { method } = config;
  // the data send before
  let data;

  function getParamsSymbolData (params: object): string {
    let data = '';
    if (/\?/.test(url)) {
      const part = url.split('?');
      url = part[0];
      data += part[1];
    }
    if (params) {
      for (const [key, val] of Object.entries(params)) {
        if (data !== '') data += '&';
        data += `${key}=${val}`;
      }
    }
    return data;
  }

  function getDataSymbolData (data: object): string {
    return isDef(data) ? JSON.stringify(data) : '';
  }

  // get the sent data
  switch (sendDataWay(method)) {
    case 'params':
      data = getParamsSymbolData(config.params);
      break;
    case 'data':
      data = getDataSymbolData(config.data);
      break;
    case 'both':
      data = getParamsSymbolData(config.params) || getDataSymbolData(config.params);
  }

  return `method=${method}&url=${url}&data=${data}`;
}

export function notUndef<T = any, D = any> (targetVal: T, defaultVal: D): T | D {
  return typeof targetVal === 'undefined' ? defaultVal : targetVal;
}

export function log (msg: string) {
  console.log(`[axios-sugar]: ${msg}.`);
}

export function isDev () {
  return process.env.NODE_ENV === 'development';
}

export function capitalize (str: string): string {
  return str.substring(0, 1).toUpperCase() + str.substring(1);
}

export function isDef (value: any): boolean {
  return typeof value !== 'undefined';
}

export function isStr (value: any): boolean {
  return typeof value === 'string';
}

export function getDurationMS (a: number, b: number): number {
  return a - b;
}
